insert into Dictionary (reason, description) values ("3d_secure_failed", "Не пройдена аутентификация по 3-D Secure. При новой попытке оплаты пользователю следует пройти аутентификацию, использовать другое платежное средство или обратиться в банк за уточнениями");
insert into Dictionary (reason, description) values ("call_issuer", "Оплата данным платежным средством отклонена по неизвестным причинам. Пользователю следует обратиться в организацию, выпустившую платежное средство");
insert into Dictionary (reason, description) values ("card_expired", "Истек срок действия банковской карты. При новой попытке оплаты пользователю следует использовать другое платежное средство");
insert into Dictionary (reason, description) values ("country_forbidden", "Нельзя заплатить банковской картой, выпущенной в этой стране. При новой попытке оплаты пользователю следует использовать другое платежное средство.Вы можете настроить ограничения на оплату иностранными банковскими картами");
insert into Dictionary (reason, description) values ("expired_on_confirmation", "Истек срок оплаты: пользователь не подтвердил платеж за время, отведенное на оплату выбранным способом. Если пользователь еще хочет оплатить, вам необходимо повторить платеж с новым ключом идемпотентности, а пользователю — подтвердить его");
insert into Dictionary (reason, description) values ("fraud_suspected", "Платеж заблокирован из-за подозрения в мошенничестве. При новой попытке оплаты пользователю следует использовать другое платежное средство");
insert into Dictionary (reason, description) values ("general_decline", "Причина не детализирована. Пользователю следует обратиться к инициатору отмены платежа за уточнением подробностей");
insert into Dictionary (reason, description) values ("identification_required", "Превышены ограничения на платежи для кошелька ЮMoney. При новой попытке оплаты пользователю следует идентифицировать кошелек или выбрать другое платежное средство");
insert into Dictionary (reason, description) values ("insufficient_funds", "Не хватает денег для оплаты. Пользователю следует пополнить баланс или использовать другое платежное средство");
insert into Dictionary (reason, description) values ("internal_timeout", "Технические неполадки на стороне ЮKassa: не удалось обработать запрос в течение 30 секунд. Повторите платеж с новым ключом идемпотентности");
insert into Dictionary (reason, description) values ("invalid_card_number", "Неправильно указан номер карты. При новой попытке оплаты пользователю следует ввести корректные данные");
insert into Dictionary (reason, description) values ("invalid_csc", "Неправильно указан код CVV2 (CVC2, CID). При новой попытке оплаты пользователю следует ввести корректные данные");
insert into Dictionary (reason, description) values ("issuer_unavailable", "Организация, выпустившая платежное средство, недоступна. При новой попытке оплаты пользователю следует использовать другое платежное средство или повторить оплату позже");
insert into Dictionary (reason, description) values ("payment_method_limit_exceeded", "Исчерпан лимит платежей для данного платежного средства или вашего магазина. При новой попытке оплаты пользователю следует использовать другое платежное средство или повторить оплату на следующий день");
insert into Dictionary (reason, description) values ("payment_method_restricted", "Запрещены операции данным платежным средством (например, карта заблокирована из-за утери, кошелек — из-за взлома мошенниками). Пользователю следует обратиться в организацию, выпустившую платежное средство");
insert into Dictionary (reason, description) values ("permission_revoked", "Нельзя провести безакцептное списание: пользователь отозвал разрешение на автоплатежи. Если пользователь еще хочет оплатить, вам необходимо создать новый платеж, а пользователю — подтвердить оплату");
insert into Dictionary (reason, description) values ("unsupported_mobile_operator", "Нельзя заплатить с номера телефона этого мобильного оператора. При новой попытке оплаты пользователю следует использовать другое платежное средство. Список поддерживаемых операторов");
